const express = require("express"); const app = express(); const PORT = process.env.PORT || 5000; app.use(express.json()); app.get("/", (req, res) => res.send("ACE CRM API Running")); app.get("/api/health", (req, res) => res.json({status: "healthy", timestamp: new Date()})); app.listen(PORT, () => console.log("Server running on port " + PORT));
